package chaptertwo;

public class ConvertionCls {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b;
		int a=257;
		double c=323.451;
		
		System.out.println("\n convertion byte to int");
		b=(byte)a;
		System.out.println("b "+b+" a "+a);
		System.out.println("\n convertion int to double");
		a=(int)c;
		System.out.println("a "+a+" c "+c);
		
		System.out.println("\n convertion byte to double ");
		b=(byte)c;
		System.out.println("b "+b+" c z"+c);
		
		
		

	}

}
