package newjavaprogram;

public class modulusoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		        int x = 42;
		        double y = 42.25;
		        
		        int c = x % 10;
		        
		        System.out.println("X mod 10: " + c);

		        // Modulus operation with a double
		        double d = y % 10;
		        System.out.println("Y mod 10: " + d);
		   
	}

}
